import React, { useState, useEffect } from 'react';
import { API } from '../services/';

const AllTheMenusPage = (props) => {
  const [menu, setMenu] = useState([]);

  useEffect(() => {
    API.GET('/menuroom')
      .then((promise) => promise.json())
      .then((result) => {
        const processedResult = result.map((r) => {
          const breakfast = Object.values(result.menu[0].breakfast);
          const lunch = Object.values(result.menu[1].lunch);
          const evening = Object.values(result.menu[2].evening);
          return {
            ...r,
            menu: [...breakfast, ...lunch, ...evening],
          };
        });

        setMenu(processedResult);
      });
    return () => {};
  }, []);

  return (
    <div>
      {menu
        .filter((m) => m.roomid)
        .map((user) => (
          <ul>
            <li>
              {user.roomid}
              {user.menu}
            </li>
          </ul>
        ))}
    </div>
  );
};

export default AllTheMenusPage;
